import 'package:hygeia/users/controller/userPreferences.dart';import 'package:hygeia/users/data/model/patient.dart';import 'package:flutter/foundation.dart';class UserProvider with ChangeNotifier {  Patient _patient = new Patient();  Patient get user => _patient;  void setUser(Patient patient) {    _patient = patient;    UserPreferences().saveUser(_patient);    notifyListeners();  }}