import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';InputDecoration deco (String hint ) {  return  InputDecoration(    hintStyle: GoogleFonts.poppins(      color: Color.fromRGBO(21, 32, 43, 0.7),      fontSize: 15,    ),    alignLabelWithHint: true,    hintText: hint,    contentPadding: EdgeInsets.only(top: 22),    enabledBorder: UnderlineInputBorder(      borderSide: BorderSide(          color: Color.fromRGBO(61, 199, 201, 1)),    ),    focusedBorder: UnderlineInputBorder(      borderSide: BorderSide(          color: Color.fromRGBO(61, 199, 201, 1)),    ),    border: UnderlineInputBorder(      borderSide: BorderSide(          color: Color.fromRGBO(61, 199, 201, 1)),    ),    errorBorder: UnderlineInputBorder(      borderSide: BorderSide(          color: Color.fromRGBO(61, 199, 201, 1)),    ),    focusedErrorBorder: UnderlineInputBorder(      borderSide: BorderSide(          color: Color.fromRGBO(61, 199, 201, 1)),    ),  );}