import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:ionicons/ionicons.dart';showErrorMessage(String message,BuildContext context) {  final snackBar = SnackBar(      elevation: 0,      backgroundColor: Colors.transparent,      content:      Card(        color: Colors.redAccent,        child: Padding(            padding: EdgeInsets.all(12),            child: Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                Text(                  message,                  style: GoogleFonts.poppins(                      color: Colors.white                  ),                ),                Icon(Icons.error,color: Colors.white),              ],            )),      ));  ScaffoldMessenger.of(context).showSnackBar(snackBar);}showSuccesMessage(String message,BuildContext context) {  final snackBar = SnackBar(      elevation: 0,      backgroundColor: Colors.transparent,      duration: Duration(seconds: 2),      content:      Card(        color: Color.fromRGBO(1, 85, 156, 1),        child: Padding(            padding: EdgeInsets.all(12),            child: Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: [                Text(                  message,                  style: GoogleFonts.poppins(                      color: Colors.white                  ),                ),                Icon(Ionicons.checkmark_circle_outline,color: Colors.white),              ],            )),      ));  ScaffoldMessenger.of(context).showSnackBar(snackBar);}