import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import 'package:hygeia/users/view/auth/widget.login.dart';import 'package:top_snackbar_flutter/custom_snack_bar.dart';import 'package:top_snackbar_flutter/top_snack_bar.dart';class Forgot extends StatefulWidget {  @override  _ForgotState createState() => _ForgotState();}TextEditingController emailControlleur;class _ForgotState extends State<Forgot> {  @override  void initState() {    super.initState();    emailControlleur = TextEditingController();  }  @override  Widget build(BuildContext context) {    double height = MediaQuery.of(context).size.height;    double width = MediaQuery.of(context).size.width;    TextEditingController emailControlleur;    return SafeArea(        child: Scaffold(          resizeToAvoidBottomInset: false,          body: Container(              height: height,              width: width,              decoration: BoxDecoration(                  image: DecorationImage(                    image: AssetImage("assets/images/bgsss.png"),                    fit: BoxFit.fill,                  )),              child: Column(                children: [                  SizedBox(height: height * 0.1),                  Image.asset("assets/images/Layer2.png"),                  SizedBox(height: height * 0.06),                  Container(                    height: height * 0.348,                    width: width * 0.9,                    decoration: BoxDecoration(                      borderRadius: BorderRadius.circular(27),                      border: Border.all(                          color: Color.fromRGBO(112, 112, 112, 1), width: 0.1),                      color: Color.fromRGBO(249, 251, 251, 1),                      boxShadow: [                        BoxShadow(                          color: Color.fromRGBO(0, 0, 0, 0.16),                          spreadRadius: 2,                          blurRadius: 2,                          offset: Offset(0, 2), // changes position of shadow                        ),                      ],                    ),                    child: Padding(                      padding: EdgeInsets.all(width * 0.08),                      child: Column(                        children: [                          Text(                            "Réinitialiser mot de passe ",                            style: GoogleFonts.inter(                                color: Color.fromRGBO(1, 85, 156, 1),                                fontWeight: FontWeight.bold,                                fontSize: 23),                          ),                          SizedBox(height: height * 0.012),                          Text(                            "Un email de réinitialisation vous sera envoyer sur votre email",                            textAlign: TextAlign.center,                            style: GoogleFonts.poppins(                                height: 1.2,                                color: Color.fromRGBO(21, 32, 43, 1),                                fontWeight: FontWeight.normal,                                fontSize: 17),                          ),                          SizedBox(height: height * 0.03),                          TextFormField(                            style: GoogleFonts.poppins(                              color: Color.fromRGBO(21, 32, 43, 0.7),                              fontSize: 15,                            ),                            controller: emailControlleur,                            decoration: InputDecoration(                              hintStyle: GoogleFonts.poppins(                                color: Color.fromRGBO(21, 32, 43, 0.7),                                fontSize: 15,                              ),                              alignLabelWithHint: true,                              hintText: "Email",                              contentPadding: EdgeInsets.only(top: 22),                              enabledBorder: UnderlineInputBorder(                                borderSide: BorderSide(                                    color: Color.fromRGBO(61, 199, 201, 1)),                              ),                              focusedBorder: UnderlineInputBorder(                                borderSide: BorderSide(                                    color: Color.fromRGBO(61, 199, 201, 1)),                              ),                              border: UnderlineInputBorder(                                borderSide: BorderSide(                                    color: Color.fromRGBO(61, 199, 201, 1)),                              ),                              errorBorder: UnderlineInputBorder(                                borderSide: BorderSide(                                    color: Color.fromRGBO(61, 199, 201, 1)),                              ),                              focusedErrorBorder: UnderlineInputBorder(                                borderSide: BorderSide(                                    color: Color.fromRGBO(61, 199, 201, 1)),                              ),                            ),                          ),                          SizedBox(                            height: height * 0.035,                          ),                          CustomElevation(                            height: 39,                            child: FlatButton(                                shape: new RoundedRectangleBorder(                                    borderRadius: new BorderRadius.circular(12.0)),                                padding: EdgeInsets.symmetric(horizontal: 25),                                color: Color.fromRGBO(1, 85, 156, 1),                                onPressed: () async {                                  if (!RegExp(                                      '^[a-zA-Z][a-zA-Z]?.[a-zA-Z]*@esi-sba.dz')                                      .hasMatch(emailControlleur.text)) {                                    showTopSnackBar(                                      context,                                      CustomSnackBar.error(                                        textStyle: GoogleFonts.poppins(                                          color: Colors.white,                                        ),                                        message:                                        "Veuillez entrer une adresse e-mail valide",                                      ),                                    );                                    return;                                  }                                },                                child: Text(                                  "Envoyer",                                  style: GoogleFonts.poppins(                                    color: Colors.white,                                    fontSize: 18,                                  ),                                )),                          )                        ],                      ),                    ),                  ),                ],              )),        ));  }}